{
    "$schema": "http://json-schema.org/draft-07/schema#",
    "$id": "GST.tmf.schema.json",
    "title": "GenericSliceTemplate",
    "definitions": {
        "GenericSliceTemplateAttributes": {
            "$id": "#TMF-GST-Attributes-v16",
            "description": "Generic Slice Templates is a GSMA proposal for the attributes to be supported by Communincations Services enabled by 5G Network Slice instances.
            \n Based on GSMA Generic servce template v16 Ocotober 2018 
            \n core document is Copyright GSMA 2018 
            \n this document is a transcription into Json schema.
            \n transcription to Json schema Copyright (c) TM Forum 2019" ,
            "type": "object",
            "properties": {Service is an abstract base class for defining the Service hierarchy. All Services are characterized as either being possibly visible and usable by a Customer or not. This gives rise to the two subclasses of Service: CustomerFacingService and ResourceFacingService.",
            "type": "object",
            "properties": {
            }
            "TMF-MaximumSupportedPacketSize": {
                "$id": "#TMF-MaximumSupportedPacketSize",
                "description": "Describes the maximum packet size in Bytes supported by the slice and may be important for URLLC and MIoT case, or to indicate a supported maximum transmission unit (MTU)",
                "type": "number",
                "default" {1500},
                },
            },
            "TMF-DeterministicCommunication": {
                "$id": "#TMF-DeterministicCommunication",
                "description" : "Defines if the slice supports deterministic communication for periodic user traffic. 
                \n Periodic traffic refers to the type of traffic with periodic transmissions. 
                ,
                "type": "object",
                "properties": {
                   "availablity": {
                      "description": "describes if the slice supports deterministic communication or not
                      \n 0: not available  -	1: available",
                      "type": "boolean",
                      "default" : "false",
                   },
                   "periodicity": {
                       "description": "provides a list of periodicities in seconds supported by the slice
                       \n ISSUE  Json schema support array of strings but not numbers",
                       "type": "number",		   
                   } 				   
                }
            },
            "TMF-DownlinkThroughputPerUser":{
                "$id": "#TMF-DownlinkThroughputPerUser",
                "description": "achievable data rate in downlink that is available ubiquitously across the coverage area to a mobile user/device",
                "type": "object",
                "properties":  {
                    "MinimumDownlinkThroughput": {
                    "description": "minimum data rate supported by the slice in Kbits per UE in downlink, which is required to achieve a sufficient quality experience (dependent on the selected service type) and can be seen as a guaranteed throughput. 
                    \n If the value is 0, best effort traffic is expected where no minimum throughput is guaranteed.",
                    "type": "number",
                    "default": "0",
                    },
                    "MaximumDownlinkThroughput": {
                    "description": "Maximum data rate supported by the slice in Kbits per UE in downlink, which is required to achieve a sufficient quality experience (dependent on the selected service type) and can be seen as a guaranteed throughput. 
                    \n If the value is 0, best effort traffic is expected where no minimum throughput is guaranteed.",
                    "type": "number",
                    "default": "50",
                    },
                },					
            },
